$(document).ready(function(){$("table").footable();$('input[rel="txtTooltip"]').tooltip();var e=/^[0-4]?([0-9]){1,3}\.([0-9]){2}$/;var t=$("td#w-plan1").text();var n=$("td#w-plan2").text();var r=$("td#w-plan3").text();var i=$("td#w-plan4").text();var s=$("td#d-plan1").text();var o=$("td#d-plan2").text();var u=$("td#d-plan3").text();var a=$("td#d-plan4").text();$("input#enter-balance").keyup(function(){var f=$("input#enter-balance").val();if(f.match(e)){$("td.user-balance").text(f);$("td#w-plan1-diff").text((f-t).toFixed(2));$("td#w-plan2-diff").text((f-n).toFixed(2));$("td#w-plan3-diff").text((f-r).toFixed(2));$("td#w-plan4-diff").text((f-i).toFixed(2));$("td#d-plan1-diff").text((f-s).toFixed(2));$("td#d-plan2-diff").text((f-o).toFixed(2));$("td#d-plan3-diff").text((f-u).toFixed(2));$("td#d-plan4-diff").text((f-a).toFixed(2))}else{$("td.user-balance").text("");$("td.user-difference").text("")}})})